import React, { useState } from 'react'; // useStateをインポート
import './App.css'; // スタイルシートはそのまま
import UserProfile from './UserProfile'; // UserProfileはそのまま
function App() {
    const [count, setCount] = useState(0); // カウンターの状態を管理
    return (
        <div className="App">
            <UserProfile />
        </div>
    );
}
export default App;
