import React, { useState } from 'react'; // useStateをインポート
import './App.css'; // スタイルシートはそのまま
import { ProductList } from './ProductList'; // ProductListコンポーネントもインポート
function App() {
    const [count, setCount] = useState(0); // カウンターの状態を管理
    return (
        <div className="App">
            <header className="App-header">
                <h1>React Counter in ASP.NET Core MVC!</h1>
                <ProductList />
            </header>
        </div>
    );
}
export default App;
